import React from 'react'
import { List, Image } from 'semantic-ui-react'
import WorldDescription from './WorldDescription';
import p1 from '../../assets/Worlds/p1.png'

import { getAllWorlds } from '../../queries/queries';
import { useQuery } from '@apollo/react-hooks';


function DisplayWorlds() {
const { loading, error, data } = useQuery(getAllWorlds);

if (loading) return <p>Loading...</p>;
if (error) return <p>Error :(</p>;
    return data.worlds.map(( world ) => (
      <List size='massive'>
      <List.Item>
        <Image avatar src={ p1 } />
        <List.Content>
          <List.Header as='a'>{ world.name }</List.Header>
          <List.Description>
              <WorldDescription>{ world.name }</WorldDescription>
          </List.Description>
        </List.Content>
      </List.Item>
    </List>
      ));
}

const ListExampleImage = () => (
  <List size='massive'>
    <List.Item>
      <Image avatar src={ p1 } />
      <List.Content>
        <List.Header as='a'>{ world.name }</List.Header>
        <List.Description>
            <WorldDescription>{ world.name }</WorldDescription>
        </List.Description>
      </List.Content>
    </List.Item>
  </List>
)

export default ListExampleImage
