        name='htmlRadios'
      />
      <Form.Field
        label='Private'
        control='input'
        type='radio'
        name='htmlRadios'
      />
    </Form.Group>
    <Form.Group grouped>
      <label>Join with Moderator Approval?</label>
      <Form.Field
        label='Yes'
        control='input'
        type='radio'
        name='htmlRadios2'
      />
      <Form.Field
        label='No'
        control='input'
        type='radio'
        name='htmlRadios2'
      /> */}
    {/* </Form.Group> */}
    <Form.Field label='Description of the World' control='textarea' rows='3' value={description} onChange = {(e) => setDescription(e.target.value)} />
    <Form.Field control='button'>
      Create World
    </Form.Field>
  </Form>
)
}
export default AddWorld;
